import { Canvas } from '@react-three/fiber';
import { OrbitControls, PerspectiveCamera } from '@react-three/drei';
import { Suspense } from 'react';
import PhoneModel from './PhoneModel';
import Lights from './Lights';
import './ThreeDViewer.css';

const ThreeDViewer = ({ modelPath, productName, productColor = '#333333' }) => {
    return (
        <div className="three-d-viewer-container">
            <Canvas
                shadows
                camera={{ position: [0, 0, 5], fov: 45 }}
                style={{ background: 'linear-gradient(to bottom, #e3f2fd 0%, #ffffff 100%)' }}
            >
                <PerspectiveCamera makeDefault position={[0, 0, 4]} />
                
                <Lights />

                <Suspense fallback={<LoadingFallback />}>
                    <PhoneModel 
                        modelPath={modelPath || '/models/default-phone.glb'}
                        color={productColor}
                        scale={1.5}
                    />
                </Suspense>

                <OrbitControls 
                    enableZoom={true}
                    enablePan={true}
                    enableRotate={true}
                    minDistance={2}
                    maxDistance={10}
                    autoRotate={true}
                    autoRotateSpeed={2}
                    rotateSpeed={0.5}
                />

                {/* Grid helper */}
                <gridHelper args={[10, 10, '#888888', '#cccccc']} position={[0, -2, 0]} />
            </Canvas>

            {productName && (
                <div className="viewer-info">
                    <p>{productName}</p>
                    <span className="viewer-hint">Kéo để xoay • Cuộn để zoom</span>
                </div>
            )}
        </div>
    );
};

// Loading component
const LoadingFallback = () => {
    return (
        <mesh>
            <boxGeometry args={[1, 1, 1]} />
            <meshStandardMaterial color="#4CAF50" wireframe />
        </mesh>
    );
};

export default ThreeDViewer;
